import{_ as t,C as n,o as i,c as r,a2 as o,E as s}from"./chunks/framework.D1jjZljM.js";const f=JSON.parse('{"title":"Manufacturing Audit Turnaround | Case Study","description":"How we helped a large steel manufacturer reduce value leakage by NPR 2.5 Crore.","frontmatter":{"title":"Manufacturing Audit Turnaround | Case Study","description":"How we helped a large steel manufacturer reduce value leakage by NPR 2.5 Crore."},"headers":[],"relativePath":"resources/case-studies/manufacturing-audit.md","filePath":"resources/case-studies/manufacturing-audit.md","lastUpdated":1771764041000}'),l={name:"resources/case-studies/manufacturing-audit.md"};function u(c,e,d,h,p,g){const a=n("CallToAction");return i(),r("div",null,[e[0]||(e[0]=o('<h1 id="case-study-manufacturing-audit-turnaround" tabindex="-1">Case Study: Manufacturing Audit Turnaround <a class="header-anchor" href="#case-study-manufacturing-audit-turnaround" aria-label="Permalink to &quot;Case Study: Manufacturing Audit Turnaround&quot;">‚Äã</a></h1><p><em>Identifying 2.5 Crore in Value Leakage through Process Re-engineering</em></p><div class="meta-box"><div class="item"><strong>Industry:</strong> Manufacturing (Steel)</div><div class="item"><strong>Service:</strong> Internal Audit &amp; Process Consulting</div><div class="item"><strong>Outcome:</strong> NPR 2.5 Crore Annual Savings</div></div><hr><h2 id="üõë-the-challenge" tabindex="-1">üõë The Challenge <a class="header-anchor" href="#üõë-the-challenge" aria-label="Permalink to &quot;üõë The Challenge&quot;">‚Äã</a></h2><p>A leading steel manufacturing company in the Terai belt was facing declining margins despite rising sales. The management suspected operational inefficiencies but couldn&#39;t pinpoint the source. Their existing audit process was purely compliance-based, checking vouchers rather than processes.</p><p><strong>Key Issues Identified:</strong></p><ul><li>High raw material consumption (burning loss) compared to industry standards.</li><li>Unreconciled inventory differences between the factory floor and the SAP system.</li><li>Delayed procurement cycles causing production halts.</li></ul><h2 id="üõ†Ô∏è-the-artha-approach" tabindex="-1">üõ†Ô∏è The Artha Approach <a class="header-anchor" href="#üõ†Ô∏è-the-artha-approach" aria-label="Permalink to &quot;üõ†Ô∏è The Artha Approach&quot;">‚Äã</a></h2><p>We deployed a specialized &quot;Techno-Commercial&quot; audit team comprising Chartered Accountants and an Industrial Engineer.</p><h3 id="phase-1-the-walk-through" tabindex="-1">Phase 1: The &quot;Walk-Through&quot; <a class="header-anchor" href="#phase-1-the-walk-through" aria-label="Permalink to &quot;Phase 1: The &quot;Walk-Through&quot;&quot;">‚Äã</a></h3><p>Instead of sitting in the AC office, our team spent 5 days on the factory floor. We mapped the entire production cycle from <em>Scrap Intake</em> to <em>Billet Production</em>.</p><h3 id="phase-2-data-analytics-correlation" tabindex="-1">Phase 2: Data Analytics &amp; Correlation <a class="header-anchor" href="#phase-2-data-analytics-correlation" aria-label="Permalink to &quot;Phase 2: Data Analytics &amp; Correlation&quot;">‚Äã</a></h3><p>We correlated electricity consumption data with production output.</p><ul><li><strong>Result</strong>: We found that the furnace was running at suboptimal temperatures during night shifts, leading to higher electricity cost per ton.</li></ul><h3 id="phase-3-yield-verification" tabindex="-1">Phase 3: Yield Verification <a class="header-anchor" href="#phase-3-yield-verification" aria-label="Permalink to &quot;Phase 3: Yield Verification&quot;">‚Äã</a></h3><p>We conducted a surprise physical verification of the &quot;Burning Loss&quot;.</p><ul><li><strong>Finding</strong>: The claimed burning loss was 8%, while the industry standard was 5%. The difference was being pilfered as &quot;waste&quot;.</li></ul><h2 id="üí°-the-solution" tabindex="-1">üí° The Solution <a class="header-anchor" href="#üí°-the-solution" aria-label="Permalink to &quot;üí° The Solution&quot;">‚Äã</a></h2><p>We implemented a <strong>Standard Operating Procedure (SOP)</strong> for the production floor:</p><ol><li><strong>CCTV Integration</strong>: Weighbridges were integrated with CCTV cameras to prevent weight manipulation during scrap entry.</li><li><strong>Yield Monitoring</strong>: Daily yield reports were mandated, with variances above 0.5% requiring a written explanation from the Plant Manager.</li><li><strong>Energy Audit</strong>: Shift-wise power consumption monitoring was introduced.</li></ol><h2 id="üöÄ-the-impact" tabindex="-1">üöÄ The Impact <a class="header-anchor" href="#üöÄ-the-impact" aria-label="Permalink to &quot;üöÄ The Impact&quot;">‚Äã</a></h2><p>Within 6 months of implementation:</p><ul><li><strong>Yield Improvement</strong>: Burning loss reduced from 8% to 5.2%.</li><li><strong>Cost Savings</strong>: Direct material savings of <strong>NPR 2.5 Crore</strong> annually.</li><li><strong>Process Control</strong>: The &quot;Phantom Inventory&quot; in SAP was eliminated, ensuring 99% stock accuracy.</li></ul><hr><blockquote><p>&quot;Artha Advisory didn&#39;t just audit our books; they audited our business. The insights they found on the factory floor paid for their fees 20 times over.&quot; ‚Äî <em>Managing Director, [Client Name Redacted]</em></p></blockquote>',26)),s(a,{title:"Are you losing money in your factory?",text:"Get a complimentary scoping session for an Operational Audit.",buttonText:"Book Scoping Call"})])}const y=t(l,[["render",u]]);export{f as __pageData,y as default};
