import{_ as r,o as n,c as a,a2 as o}from"./chunks/framework.D1jjZljM.js";const u=JSON.parse('{"title":"Hydropower Licensing Guide","description":"Steps to obtain Survey, Generation, and PPA licenses","frontmatter":{"title":"Hydropower Licensing Guide","description":"Steps to obtain Survey, Generation, and PPA licenses","icon":"⚡","parent":"Resources|/resources/","image":"/images/industries/ind-hydro.jpg","sidebar":false},"headers":[],"relativePath":"resources/guides/hydropower-licensing-guide.md","filePath":"resources/guides/hydropower-licensing-guide.md","lastUpdated":1771764041000}'),i={name:"resources/guides/hydropower-licensing-guide.md"};function t(s,e,l,c,p,d){return n(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="hydropower-development-licensing-ppa-guide" tabindex="-1">Hydropower Development: Licensing &amp; PPA Guide <a class="header-anchor" href="#hydropower-development-licensing-ppa-guide" aria-label="Permalink to &quot;Hydropower Development: Licensing &amp; PPA Guide&quot;">​</a></h1><p>Developing a hydropower project in Nepal is a marathon of regulatory hurdles. From the <strong>Department of Electricity Development (DoED)</strong> to the <strong>Nepal Electricity Authority (NEA)</strong>, here is your roadmap.</p><h2 id="phase-1-survey-license-1-2-years" tabindex="-1">Phase 1: Survey License (1-2 Years) <a class="header-anchor" href="#phase-1-survey-license-1-2-years" aria-label="Permalink to &quot;Phase 1: Survey License (1-2 Years)&quot;">​</a></h2><ul><li><strong>Application:</strong> Submit to DoED with a desk study map.</li><li><strong>Output:</strong> You get the exclusive right to conduct the Feasibility Study and IEE/EIA.</li><li><strong>Compliance:</strong> Must submit semi-annual progress reports to DoED.</li></ul><h2 id="phase-2-generation-license-the-big-one" tabindex="-1">Phase 2: Generation License (The Big One) <a class="header-anchor" href="#phase-2-generation-license-the-big-one" aria-label="Permalink to &quot;Phase 2: Generation License (The Big One)&quot;">​</a></h2><p>To convert your Survey License into a Generation License, you need:</p><ol><li><strong>Approved Feasibility Study:</strong> Technical vetting by DoED.</li><li><strong>EIA/IEE Approval:</strong> Environmental clearance from the Ministry of Forests &amp; Environment.</li><li><strong>Connection Agreement:</strong> A technical agreement with NEA for grid connectivity.</li><li><strong>PPA (Power Purchase Agreement):</strong> The holy grail.</li></ol><h2 id="phase-3-the-ppa-power-purchase-agreement" tabindex="-1">Phase 3: The PPA (Power Purchase Agreement) <a class="header-anchor" href="#phase-3-the-ppa-power-purchase-agreement" aria-label="Permalink to &quot;Phase 3: The PPA (Power Purchase Agreement)&quot;">​</a></h2><p>The PPA guarantees your revenue.</p><ul><li><strong>Rates (ROR Projects):</strong><ul><li>Wet Season: NPR 4.80 per unit.</li><li>Dry Season: NPR 8.40 per unit.</li></ul></li><li><strong>Take or Pay:</strong> NEA guarantees to buy your power (subject to dispatch rules).</li></ul><h2 id="phase-4-financial-close" tabindex="-1">Phase 4: Financial Close <a class="header-anchor" href="#phase-4-financial-close" aria-label="Permalink to &quot;Phase 4: Financial Close&quot;">​</a></h2><p>Banks typically finance <strong>70%</strong> of the project cost (Debt) against <strong>30%</strong> promoter equity.</p><ul><li><strong>Requirement:</strong> PPA, Generation License, and a bankable financial model.</li></ul><hr><h3 id="need-project-finance-advisory" tabindex="-1">Need Project Finance Advisory? <a class="header-anchor" href="#need-project-finance-advisory" aria-label="Permalink to &quot;Need Project Finance Advisory?&quot;">​</a></h3><p>We help developers build financial models and syndicate loans with consortium banks.</p><p><a href="/industries/hydropower"><strong>Contact Our Energy Team</strong></a></p>',17)])])}const g=r(i,[["render",t]]);export{u as __pageData,g as default};
