import{_ as a,C as i,o as n,c as r,a2 as e,E as o}from"./chunks/framework.D1jjZljM.js";const h=JSON.parse('{"title":"NFRS for SMEs in Nepal","description":"Demystifying the Nepal Financial Reporting Standards for SMEs.","frontmatter":{"title":"NFRS for SMEs in Nepal","description":"Demystifying the Nepal Financial Reporting Standards for SMEs.","icon":"ðŸ“˜","parent":"Resources|/resources/","image":"/images/headers/service-audit.jpg"},"headers":[],"relativePath":"resources/guides/nfrs-for-smes.md","filePath":"resources/guides/nfrs-for-smes.md","lastUpdated":1771764041000}'),l={name:"resources/guides/nfrs-for-smes.md"};function d(c,t,u,f,p,g){const s=i("FAQ");return n(),r("div",null,[t[0]||(t[0]=e('<h1 id="nfrs-for-smes-in-nepal-a-simplified-guide" tabindex="-1">NFRS for SMEs in Nepal: A Simplified Guide <a class="header-anchor" href="#nfrs-for-smes-in-nepal-a-simplified-guide" aria-label="Permalink to &quot;NFRS for SMEs in Nepal: A Simplified Guide&quot;">â€‹</a></h1><p><em>Demystifying the Nepal Financial Reporting Standards for Small and Medium Enterprises.</em></p><hr><p>The adoption of <strong>Nepal Financial Reporting Standards (NFRS)</strong> is no longer just for banks and multinational corporations. The Institute of Chartered Accountants of Nepal (ICAN) has rolled out specific standards tailored for <strong>Small and Medium-Sized Entities (SMEs)</strong>.</p><p>For many business owners, &quot;NFRS&quot; sounds like an expensive compliance headache. In reality, NFRS for SMEs is designed to be <strong>simpler</strong>, <strong>less costly</strong>, and <strong>more relevant</strong> than the full NFRS.</p><h2 id="applicability-benefits" tabindex="-1">Applicability &amp; Benefits <a class="header-anchor" href="#applicability-benefits" aria-label="Permalink to &quot;Applicability &amp; Benefits&quot;">â€‹</a></h2>',6)),o(s,{items:[{question:"Who Qualifies as an SME for NFRS?",answer:"An entity typically qualifies if it has <strong>No Public Accountability</strong> (not listed, not a bank/insurer) and publishes <strong>General Purpose Financial Statements</strong> for external users. <br><em>Note: Micro-entities may follow the simpler 'NAS for Micro Entities'.</em>"},{question:"Why should SMEs adopt NFRS?",answer:"<ol class='list-decimal pl-5'><li><strong>Bank Loans</strong>: Banks prefer NFRS-compliant statements for credit assessment.</li><li><strong>Comparability</strong>: Easier to compare performance with peers for investors.</li><li><strong>True Profitability</strong>: Accrual accounting provides a more accurate financial picture than cash basis.</li></ol>"}]}),t[1]||(t[1]=e('<h2 id="key-differences-full-nfrs-vs-nfrs-for-smes" tabindex="-1">Key Differences: Full NFRS vs. NFRS for SMEs <a class="header-anchor" href="#key-differences-full-nfrs-vs-nfrs-for-smes" aria-label="Permalink to &quot;Key Differences: Full NFRS vs. NFRS for SMEs&quot;">â€‹</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Feature</th><th style="text-align:left;">Full NFRS</th><th style="text-align:left;">NFRS for SMEs</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Complexity</strong></td><td style="text-align:left;">High complexity; covers all complex financial instruments.</td><td style="text-align:left;">Simplified; removes complex options irrelevant to SMEs.</td></tr><tr><td style="text-align:left;"><strong>Disclosure</strong></td><td style="text-align:left;">Extensive disclosure requirements (3,000+ items).</td><td style="text-align:left;">Significantly reduced disclosures (~300 items).</td></tr><tr><td style="text-align:left;"><strong>Fair Value</strong></td><td style="text-align:left;">Heavy reliance on &quot;Fair Value&quot; accounting.</td><td style="text-align:left;">Mostly uses &quot;Historical Cost&quot; (easier to track).</td></tr><tr><td style="text-align:left;"><strong>Goodwill</strong></td><td style="text-align:left;">Tested for impairment annually (complex).</td><td style="text-align:left;">Amortized over 10 years (simple).</td></tr><tr><td style="text-align:left;"><strong>Borrowing Costs</strong></td><td style="text-align:left;">Must be capitalized (added to asset cost).</td><td style="text-align:left;">Must be expensed immediately (simpler).</td></tr><tr><td style="text-align:left;"><strong>R&amp;D Costs</strong></td><td style="text-align:left;">Development costs must be capitalized.</td><td style="text-align:left;">All research &amp; development costs are expensed.</td></tr></tbody></table><h2 id="common-challenges-in-transition" tabindex="-1">Common Challenges in Transition <a class="header-anchor" href="#common-challenges-in-transition" aria-label="Permalink to &quot;Common Challenges in Transition&quot;">â€‹</a></h2><p>Transitioning to NFRS for SMEs does require clear steps:</p><ul><li><strong>First-Time Adoption</strong>: You must prepare an &quot;Opening Balance Sheet&quot; that restates your previous year&#39;s figures under the new rules.</li><li><strong>Staff Training</strong>: Your internal accountant may need training to understand concepts like &quot;accruals&quot; vs. &quot;cash flow&quot;.</li><li><strong>Software</strong>: Ensure your accounting software (Tally, Swastik, QuickBooks) is configured to generate the required reports.</li></ul><h2 id="conclusion" tabindex="-1">Conclusion <a class="header-anchor" href="#conclusion" aria-label="Permalink to &quot;Conclusion&quot;">â€‹</a></h2><p>NFRS for SMEs is a &quot;right-sized&quot; framework. It brings the credibility of international standards without the crushing weight of complex compliance. Embracing it early positions your business as transparent, professional, and ready for growth.</p><hr><h3 id="ready-to-transition" tabindex="-1">Ready to Transition? <a class="header-anchor" href="#ready-to-transition" aria-label="Permalink to &quot;Ready to Transition?&quot;">â€‹</a></h3><p>Our audit team can help you map your current accounts to NFRS for SMEs without disrupting your daily operations.</p><p><a href="/contact"><strong>Get a Consultation</strong></a></p>',11))])}const y=a(l,[["render",d]]);export{h as __pageData,y as default};
