import{_ as e,o as a,c as o,a2 as i}from"./chunks/framework.D1jjZljM.js";const h=JSON.parse('{"title":"NGO & INGO Compliance Handbook","description":"A guide to the regulatory framework for non-profits in Nepal.","frontmatter":{"title":"NGO & INGO Compliance Handbook","description":"A guide to the regulatory framework for non-profits in Nepal.","icon":"ü§ù","image":"/images/industries/ind-ngo.jpg","parent":"Resources|/resources/","sidebar":false},"headers":[],"relativePath":"resources/guides/ngo-compliance-handbook.md","filePath":"resources/guides/ngo-compliance-handbook.md","lastUpdated":1771764041000}'),n={name:"resources/guides/ngo-compliance-handbook.md"};function r(l,t,s,d,g,c){return a(),o("div",null,[...t[0]||(t[0]=[i('<h1 id="ngo-ingo-compliance-handbook" tabindex="-1">NGO &amp; INGO Compliance Handbook <a class="header-anchor" href="#ngo-ingo-compliance-handbook" aria-label="Permalink to &quot;NGO &amp; INGO Compliance Handbook&quot;">‚Äã</a></h1><p>Operating a non-profit in Nepal involves navigating a complex web of regulators: the <strong>Social Welfare Council (SWC)</strong>, <strong>District Administration Office (DAO)</strong>, and the <strong>Inland Revenue Department (IRD)</strong>.</p><p>This handbook simplifies your compliance obligations.</p><h2 id="_1-legal-registration-renewal" tabindex="-1">1. Legal Registration &amp; Renewal <a class="header-anchor" href="#_1-legal-registration-renewal" aria-label="Permalink to &quot;1. Legal Registration &amp; Renewal&quot;">‚Äã</a></h2><h3 id="ngos-local" tabindex="-1"><strong>NGOs (Local)</strong> <a class="header-anchor" href="#ngos-local" aria-label="Permalink to &quot;**NGOs (Local)**&quot;">‚Äã</a></h3><ul><li><strong>Registration:</strong> At the District Administration Office (DAO).</li><li><strong>Renewal:</strong> Must be renewed annually (every Fiscal Year).</li><li><strong>Key Document:</strong> Tax Clearance Certificate is mandatory for renewal.</li></ul><h3 id="ingos-international" tabindex="-1"><strong>INGOs (International)</strong> <a class="header-anchor" href="#ingos-international" aria-label="Permalink to &quot;**INGOs (International)**&quot;">‚Äã</a></h3><ul><li><strong>General Agreement (GA)</strong>: A 3-5 year framework agreement signed with the SWC.</li><li><strong>Project Agreement (PA)</strong>: Specific agreement for each project, detailing the budget and district-wise activities.</li></ul><h2 id="_2-tax-compliance-the-big-confusion" tabindex="-1">2. Tax Compliance (The Big Confusion) <a class="header-anchor" href="#_2-tax-compliance-the-big-confusion" aria-label="Permalink to &quot;2. Tax Compliance (The Big Confusion)&quot;">‚Äã</a></h2><p>Many NGOs assume they are &quot;Tax Exempt.&quot; <strong>This is false.</strong></p><ul><li><strong>Income Tax:</strong> NGOs are tax-exempt <em>only on donations/grants</em> if they have a Tax Exemption Certificate (Dafa 2) from the IRD.</li><li><strong>Commercial Income:</strong> If an NGO sells products (e.g., handicrafts) or consultancy, that income is <strong>taxable</strong>.</li><li><strong>TDS Obligations:</strong> NGOs must deduct and deposit TDS on: <ul><li>House Rent (10%)</li><li>Staff Salaries (1% Social Security Tax)</li><li>Consultancy Fees (15%)</li><li>Audit Fees (15%)</li></ul></li></ul><h2 id="_3-swc-audit-requirements" tabindex="-1">3. SWC Audit Requirements <a class="header-anchor" href="#_3-swc-audit-requirements" aria-label="Permalink to &quot;3. SWC Audit Requirements&quot;">‚Äã</a></h2><p>The Social Welfare Council has specific audit formats diverging from standard NAS.</p><h3 id="the-project-audit-report" tabindex="-1"><strong>The &quot;Project Audit&quot; Report</strong> <a class="header-anchor" href="#the-project-audit-report" aria-label="Permalink to &quot;**The &quot;Project Audit&quot; Report**&quot;">‚Äã</a></h3><p>Unlike a standard financial audit, a Project Audit must verify:</p><ul><li><strong>Administrative vs. Program Cost:</strong> Administrative costs must not exceed <strong>20%</strong> of the total budget.</li><li><strong>District-Level Expenditure:</strong> Proof that funds were actually spent in the target district.</li><li><strong>Social Audit:</strong> A participatory audit involving stakeholders (beneficiaries) to assess impact.</li></ul><h2 id="_4-reporting-calendar" tabindex="-1">4. Reporting Calendar <a class="header-anchor" href="#_4-reporting-calendar" aria-label="Permalink to &quot;4. Reporting Calendar&quot;">‚Äã</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Deadline</th><th style="text-align:left;">Obligation</th><th style="text-align:left;">Regulator</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Ashwin End</strong></td><td style="text-align:left;">Annual Audit Report submission</td><td style="text-align:left;">IRD (Tax Office)</td></tr><tr><td style="text-align:left;"><strong>Poush End</strong></td><td style="text-align:left;">NGO Renewal</td><td style="text-align:left;">District Administration Office (DAO)</td></tr><tr><td style="text-align:left;"><strong>Quarterly</strong></td><td style="text-align:left;">Progress Report</td><td style="text-align:left;">Social Welfare Council (SWC)</td></tr><tr><td style="text-align:left;"><strong>Every 6 Months</strong></td><td style="text-align:left;">Project Performance Report</td><td style="text-align:left;">SWC</td></tr></tbody></table><hr><h3 id="struggling-with-swc-approval" tabindex="-1">Struggling with SWC Approval? <a class="header-anchor" href="#struggling-with-swc-approval" aria-label="Permalink to &quot;Struggling with SWC Approval?&quot;">‚Äã</a></h3><p>Our specialized &quot;Development Sector&quot; team helps you draft GAs/PAs and clear project audits.</p><p><a href="/industries/ngo-ingo"><strong>Contact Our NGO Desk</strong></a></p>',22)])])}const p=e(n,[["render",r]]);export{h as __pageData,p as default};
