import{_ as t,o as i,c as o,a2 as a}from"./chunks/framework.D1jjZljM.js";const h=JSON.parse(`{"title":"Director's Statutory Duty & Compliance Checklist","description":"A comprehensive checklist for Company Directors in Nepal to ensure compliance with the Companies Act 2063.","frontmatter":{"title":"Director's Statutory Duty & Compliance Checklist","description":"A comprehensive checklist for Company Directors in Nepal to ensure compliance with the Companies Act 2063.","layout":"doc","icon":"âœ…","parent":"Templates|/resources/templates/","sidebar":false},"headers":[],"relativePath":"resources/templates/directors-duty-checklist.md","filePath":"resources/templates/directors-duty-checklist.md","lastUpdated":1771764041000}`),n={name:"resources/templates/directors-duty-checklist.md"};function r(s,e,l,c,d,p){return i(),o("div",null,[...e[0]||(e[0]=[a('<h1 id="director-s-statutory-duty-checklist" tabindex="-1">Director&#39;s Statutory Duty Checklist <a class="header-anchor" href="#director-s-statutory-duty-checklist" aria-label="Permalink to &quot;Director&#39;s Statutory Duty Checklist&quot;">â€‹</a></h1><p>As a Director of a company in Nepal, you bear significant legal responsibilities. Use this checklist to ensure you are meeting your statutory obligations under the <em>Companies Act, 2063</em> and preventing personal liability.</p><h2 id="_1-general-statutory-duties-section-134" tabindex="-1">1. General Statutory Duties (Section 134) <a class="header-anchor" href="#_1-general-statutory-duties-section-134" aria-label="Permalink to &quot;1. General Statutory Duties (Section 134)&quot;">â€‹</a></h2><ul><li>[ ] <strong>Act Honestly:</strong> I have acted honestly and in good faith for the benefit of the company.</li><li>[ ] <strong>No Personal Conflict:</strong> I have not used my position to gain personal benefit or cause loss to the company.</li><li>[ ] <strong>Disclosure of Interest:</strong> I have disclosed any personal interest in transactions entered into by the company.</li><li>[ ] <strong>Care and Diligence:</strong> I have exercised the degree of care and diligence that a prudent person would exercise in similar circumstances.</li></ul><h2 id="_2-mandatory-filings-meetings" tabindex="-1">2. Mandatory Filings &amp; Meetings <a class="header-anchor" href="#_2-mandatory-filings-meetings" aria-label="Permalink to &quot;2. Mandatory Filings &amp; Meetings&quot;">â€‹</a></h2><ul><li>[ ] <strong>Annual General Meeting (AGM):</strong> Held within 6 months of the end of the fiscal year.</li><li>[ ] <strong>Annual Returns:</strong> Filed with the Office of Company Registrar (OCR) within 30 days of the AGM.</li><li>[ ] <strong>Auditor Appointment:</strong> Appointed a qualified auditor at the AGM and notified the OCR within 15 days.</li><li>[ ] <strong>Share Register:</strong> Maintained an up-to-date register of shareholders and debenture holders.</li></ul><h2 id="_3-financial-integrity" tabindex="-1">3. Financial Integrity <a class="header-anchor" href="#_3-financial-integrity" aria-label="Permalink to &quot;3. Financial Integrity&quot;">â€‹</a></h2><ul><li>[ ] <strong>Solvency:</strong> ensured the company can pay its debts as they fall due.</li><li>[ ] <strong>Proper Accounts:</strong> Maintained proper books of accounts preventing fraud and error.</li><li>[ ] <strong>No Loans to Directors:</strong> ensured the company has NOT provided loans or guarantees to directors or their close relatives (unless explicitly permitted for public companies with banking approval).</li></ul><h2 id="_4-prohibited-acts-to-avoid-personal-liability" tabindex="-1">4. Prohibited Acts (To Avoid Personal Liability) <a class="header-anchor" href="#_4-prohibited-acts-to-avoid-personal-liability" aria-label="Permalink to &quot;4. Prohibited Acts (To Avoid Personal Liability)&quot;">â€‹</a></h2><ul><li>[ ] <strong>Misdescription:</strong> The company&#39;s full name is clearly displayed on all letters, invoices, and notices. (Personal liability attaches if omitted).</li><li>[ ] <strong>Fraudulent Trading:</strong> The business is not being carried on with the intent to defraud creditors.</li><li>[ ] <strong>Prospectus Misstatement:</strong> (For Public Companies) All statements in the prospectus are true and not misleading.</li></ul><h2 id="_5-specific-to-public-companies" tabindex="-1">5. Specific to Public Companies <a class="header-anchor" href="#_5-specific-to-public-companies" aria-label="Permalink to &quot;5. Specific to Public Companies&quot;">â€‹</a></h2><ul><li>[ ] <strong>Minimum Directors:</strong> The Board consists of at least 3 directors (max 11).</li><li>[ ] <strong>Independent Director:</strong> (If listed) At least one independent director is appointed.</li><li>[ ] <strong>Cooling Off:</strong> Ensuring the auditor appointed has not worked as an employee/partner in the last 3 years.</li></ul><div class="warning custom-block"><p class="custom-block-title">Liability Alert</p><p>A director who contravenes these duties may be personally liable for any loss suffered by the company and may face fines up to Rs. 50,000 or imprisonment up to 2 years under Section 160 of the Companies Act.</p></div><p>ðŸ’¡ <strong>Tip:</strong> Print this page (Ctrl+P) or copy the checklist above into your document editor to use in Board meetings.</p>',14)])])}const m=t(n,[["render",r]]);export{h as __pageData,m as default};
