import{_ as a,o as r,c as e,a2 as n}from"./chunks/framework.D1jjZljM.js";const u=JSON.parse('{"title":"NFRS & Financial Reporting Advisory","description":"Transitioning legacy accounting to Nepal Financial Reporting Standards (NFRS) for better transparency.","frontmatter":{"title":"NFRS & Financial Reporting Advisory","description":"Transitioning legacy accounting to Nepal Financial Reporting Standards (NFRS) for better transparency.","icon":"ðŸ“ˆ","parent":"Services|/services/","layout":"doc"},"headers":[],"relativePath":"services/tax/nfrs-advisory.md","filePath":"services/tax/nfrs-advisory.md","lastUpdated":1771764041000}'),i={name:"services/tax/nfrs-advisory.md"};function o(s,t,l,d,c,p){return r(),e("div",null,[...t[0]||(t[0]=[n('<p>Nepal&#39;s transition to international accounting standards demands professional precision. <strong>Artha Advisory</strong> helps companies navigate the complexity of NFRS implementation, ensuring your financial reporting is transparent, credible, and compliant with global best practices.</p><h2 id="the-nfrs-framework-in-nepal" tabindex="-1">The NFRS Framework in Nepal <a class="header-anchor" href="#the-nfrs-framework-in-nepal" aria-label="Permalink to &quot;The NFRS Framework in Nepal&quot;">â€‹</a></h2><table tabindex="0"><thead><tr><th>Framework</th><th>Target Group</th></tr></thead><tbody><tr><td>Banks, Insurance, Listed Companies</td><td><strong>Full NFRS</strong></td></tr><tr><td>Large unlisted companies</td><td><strong>Full NFRS</strong></td></tr><tr><td>SMEs (below threshold)</td><td><strong>NFRS for SMEs</strong></td></tr><tr><td>Micro entities</td><td><strong>NAS (Nepal Accounting Standards)</strong></td></tr></tbody></table><h2 id="our-advisory-solutions" tabindex="-1">Our Advisory Solutions <a class="header-anchor" href="#our-advisory-solutions" aria-label="Permalink to &quot;Our Advisory Solutions&quot;">â€‹</a></h2><h3 id="_1-nfrs-transition-gap-analysis" tabindex="-1">1. NFRS Transition &amp; Gap Analysis <a class="header-anchor" href="#_1-nfrs-transition-gap-analysis" aria-label="Permalink to &quot;1. NFRS Transition &amp; Gap Analysis&quot;">â€‹</a></h3><p>Reviewing your current accounting policies and identifying the necessary adjustments required to align with NFRS standards.</p><h3 id="_2-first-time-adoption-nfrs-1" tabindex="-1">2. First-Time Adoption (NFRS 1) <a class="header-anchor" href="#_2-first-time-adoption-nfrs-1" aria-label="Permalink to &quot;2. First-Time Adoption (NFRS 1)&quot;">â€‹</a></h3><p>Managing the complex &quot;Day 1&quot; adjustments needed when a company moves from legacy standards to NFRS for the first time.</p><h3 id="_3-technical-paper-writing" tabindex="-1">3. Technical Paper Writing <a class="header-anchor" href="#_3-technical-paper-writing" aria-label="Permalink to &quot;3. Technical Paper Writing&quot;">â€‹</a></h3><p>Drafting the required accounting technical papers for your auditors to justify the valuation methods and assumptions used in your financial reports.</p><hr><h2 id="why-nfrs-matters" tabindex="-1">Why NFRS Matters? <a class="header-anchor" href="#why-nfrs-matters" aria-label="Permalink to &quot;Why NFRS Matters?&quot;">â€‹</a></h2><ul><li><strong>Transparency</strong>: Provides a clearer picture of financial health to stakeholders and investors.</li><li><strong>Comparability</strong>: Allows your business to be benchmarked against international peers.</li><li><strong>Creditability</strong>: Increases confidence for banks and foreign investors looking to fund your growth.</li></ul><div class="tip custom-block"><p class="custom-block-title">Training</p><p>We also offer 40-hour NFRS training programs for your finance team to help them internalize the new standards. <a href="/about/">Learn More About Training</a> or <a href="/contact">Request an NFRS Assessment</a>.</p></div>',14)])])}const g=a(i,[["render",o]]);export{u as __pageData,g as default};
